import React from 'react'
import Head from 'next/head'

const getPokemon = async (q) => {
  const data = await fetch(`/api/search?q=${escape(q)}`).then(res => res.json());
  return data;
}

const usePokemon = (query) => {
  const [pokemons, setPokemons] = React.useState(null);

  React.useEffect(() => {
    if (!query) return;

    const pokemons = getPokemon(query);
    setPokemons(pokemons);
  }, [query]);

  return pokemons;
}


export default function Home() {
  const [query, setQuery] = React.useState('');
  const pokemons = usePokemon(query);

  console.log(pokemons);

  return (
    <div className="container">
      <Head>
        <title>Pokemon</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main>
        <input placeholder="Search for a pokemon" type="search" value={query} onChange={event => {
          setQuery(event.target.value);
          getPokemon(event.target.value);
        }}/>
      </main>
    </div>
  )
}
